using System; using NeoSharp.Crypto; using NeoSharp.Utils; using NeoSharp.Script; using NeoSharp.Types; using NeoSharp.Tests.Helpers; class DebugTest { static void Main() { var privateKeyHex = TestConstants.DefaultAccountPrivateKey; var expectedPublicKey = TestConstants.DefaultAccountPublicKey; var expectedVerificationScript = TestConstants.DefaultAccountVerificationScript; var expectedScriptHash = TestConstants.DefaultAccountScriptHash; var expectedAddress = TestConstants.DefaultAccountAddress; Console.WriteLine($"Expected Public Key: {expectedPublicKey}"); Console.WriteLine($"Expected Verification Script: {expectedVerificationScript}"); var privateKeyBytes = privateKeyHex.FromHexString(); var keyPair = new ECKeyPair(privateKeyBytes); var actualPublicKeyBytes = keyPair.PublicKey.GetEncoded(true); var actualPublicKey = actualPublicKeyBytes.ToHexString(); var actualVerificationScript = ScriptBuilder.BuildVerificationScript(actualPublicKeyBytes); var actualVerificationScriptHex = actualVerificationScript.ToHexString(); Console.WriteLine($"Actual Public Key: {actualPublicKey}"); Console.WriteLine($"Actual Verification Script: {actualVerificationScriptHex}"); Console.WriteLine($"Public Key Match: {actualPublicKey == expectedPublicKey}"); Console.WriteLine($"Verification Script Match: {actualVerificationScriptHex == expectedVerificationScript}"); } }
